import React, { useState } from 'react';
import {
  DesktopOutlined,
  PieChartOutlined,
  UserOutlined,
} from '@ant-design/icons';
import { Layout, Menu, Button, Card, theme } from 'antd';
const { Header, Content, Footer, Sider } = Layout;

function getItem(label, key, icon, children) {
  return {
    key,
    icon,
    children,
    label,
  };
}

const items = [
  getItem('Home', '1', <PieChartOutlined />),
  getItem('Stats', '2', <DesktopOutlined />),
  getItem('User', 'sub1', <UserOutlined />, [
    getItem('Tom', '3'),
    getItem('Bill', '4'),
    getItem('Alex', '5'),
  ]),
];

const products = [
  { id: 1, name: 'Product 1', description: 'This is product 1', price: '$10' },
  { id: 2, name: 'Product 2', description: 'This is product 2', price: '$20' },
  { id: 3, name: 'Product 3', description: 'This is product 3', price: '$30' },
  { id: 4, name: 'Product 4', description: 'This is product 4', price: '$40' },
];

const App = () => {
  const [collapsed, setCollapsed] = useState(false);
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)}>
        <div className="demo-logo-vertical" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} />
      </Sider>
      <Layout>
        <Header
          style={{
            padding: 0,
            background: colorBgContainer,
            borderRadius: borderRadiusLG,
            textAlign: 'center',
            fontWeight: 'bold',
            fontSize: '1.5rem',
          }}
        >
          Shop
        </Header>
        <Content style={{ margin: '0 16px' }}>
          <div
            style={{
              padding: 24,
              minHeight: 600,
              background: colorBgContainer,
              borderRadius: borderRadiusLG,
            }}
          >
            <div
              style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '1rem',
              }}
              className="content"
            >
              {products.map((product) => (
                <Card
                  key={product.id}
                  title={product.name}
                  style={{ gridArea: 'auto', borderRadius: '10px' }}
                >
                  <p>{product.description}</p>
                  <p>{product.price}</p>
                  <Button type="primary" style={{ marginRight: '8px' }}>
                    Buy
                  </Button>
                  <Button>Add to Cart</Button>
                </Card>
              ))}
            </div>
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          Midanka Lahon @2024
        </Footer>
      </Layout>
    </Layout>
  );
};

export default App;
